import React, { useState } from 'react';
import Web3 from 'web3';
import { simpleStorageAbi } from './abi/abis';
import './App.css';
const web3 = new Web3(Web3.givenProvider);
// contract address is provided by Truffle migration
const contractAddr = '0xd9145CCE52D386f254917e481eB44e9943F39138';
const SimpleContract = new web3.eth.Contract(simpleStorageAbi, contractAddr);
function App() {
  const [name, setName] = useState();
  const [getName, setGetName] = useState();

  //const [dept, setDept] = useState();
  //const [getDept, setGetDept] = useState();

  const handleSet = async (e) => {
    e.preventDefault();    
    const accounts = await window.ethereum.enable();
    const account = accounts[0];
    const gas = await SimpleStorage.methods.set(name).estimateGas();
    const result = await SimpleStorage.methods.set(name).send({
      from: account,
      gas 
    })
    console.log(result);
  }

  const handleGet = async (e) => {
    e.preventDefault();
    const result = await SimpleContract.methods.get().call();
    setGetName(result);
    console.log(result);
  }

return (
  <div className="App">
    <header className="App-header">
      <form onSubmit={handleSet}>
        <label>
          Name:
          <input 
            type="text"
            name="name"
            value={name}
            onChange={ e => setName(e.target.value) } />
        </label>
        <input type="submit" value="Set Name" />
      </form>
      <br/>
      <button
        onClick={handleGet}
        type="button" > 
        Get Name 
      </button>
      { getName }
    </header>
  </div>  
);
};
export default App;
